(ns advent.day-03
  (:require [clojure.string :as str]))


(defn priority-of [character]
  (let [pri-1-through-52 "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ"]
    (inc (str/index-of pri-1-through-52 character))))

(defn unique-char-single-rucksack [input]
  (let [[left right] (partition (/ (count input) 2) input)]
    (first (clojure.set/intersection (set left) (set right)))))

(defn answer [raw]
  (let [lines (str/split-lines raw)]
    (->> lines
         (map unique-char-single-rucksack)
         (map priority-of)
         (reduce +))))

(defn unique-char-three-rucksacks [[a b c]]
  (first (clojure.set/intersection (set a) (set b) (set c))))

(defn answer-part-2 [raw]
  (let [lines (str/split-lines raw)]
    (->> (partition 3 lines)
         (map unique-char-three-rucksacks)
         (map priority-of)
         (reduce +))))

