(ns advent.day-06)

(defn answer [raw distinct-marker-count]
  (loop [stream raw
         i 1
         curr ""]
    (let [c (first stream)
          candidate (concat curr (str c))]
      (cond
        (empty? stream)
        nil

        (= (count (set candidate)) distinct-marker-count)
        i

        :else
        (recur
          (rest stream)
          (inc i)
          (take-last (dec distinct-marker-count) candidate))))))

